private class XORShift128
{
    private UInt32 x = 123456789;
    private UInt32 y = 362436069;
    private UInt32 z = 521288629;
    private UInt32 w = 88675123;

    public XORShift128(UInt32 x, UInt32 y)
    {
        this.x = x;
        this.y = y;
    }

    public UInt32 next()
    {
        UInt32 t = (x ^ (x << 11)) & 0xffffffff;
        x = y;
        y = z;
        z = w;
        w = (w ^ (w >> 19) ^ (t ^ (t >> 8)));
        return w;
    }
}

