def RomanToDecimal(roman):
    DecimalValue=0 
    i = 0 # Position in string

    while i < len(roman):

        # Test for 2 char first    
        if i < len(roman)-1: # Make sure we're not on the last letter
            # Get current + next letters
            s = roman[i:i+2] 
            add = False

            if s == "CM":
                add = 900
            elif s == "IV":
                add = 4
            elif s == "IX":
                add = 9
            elif s == "XL":
                add = 40
            elif s == "XC":
                add = 90
            elif s == "CD":
                add = 400

            if add: # We've found a match
                i += 2 # Step 2
                DecimalValue += add # Add the value
                continue # Next loop

        # If we get here the 2 character match failed
        # Single char match
        s = roman[i] 

        if s == "I":
            DecimalValue += 1
        elif s == "V":
            DecimalValue += 5
        elif s == "X":
            DecimalValue += 10
        elif s == "L":
            DecimalValue += 50
        elif s == "C":
            DecimalValue += 100
        elif s == "D":
            DecimalValue += 500
        elif s == "M":
            DecimalValue += 1000

        i += 1

    return DecimalValue

